// Login.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../App.css';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const history = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/login', { email, password });
      const token = response.data.token;
      localStorage.setItem('token', token);
      // Determine the role of the logged-in user
      const decodedToken = parseJwt(token);
      const userRole = decodedToken.role;
     

      // Redirect to the appropriate page based on the user's role
      if (userRole === 'manufacturer') {
        history(`/manufacturer/${decodedToken.email}/messages`);
      } else if (userRole === 'transporter') {
        history(`/transporter/${decodedToken.email}/messages`);
      } else {
        // Handle other roles or fallback to a default page
        history('/');
      }
      
    } catch (error) {
      console.error(error);
    }
  };

  // Function to parse the JWT token and extract the role
    const parseJwt = (token) => {
    const base64Url = token.split('.')[1];
    const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
    const jsonPayload = decodeURIComponent(
      atob(base64)
        .split('')
        .map(function (c) {
          return '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2);
        })
        .join('')
    );

    return JSON.parse(jsonPayload);
  };  

  const redirectToRegister = () => {
    history('/registration');
  }

  return (
    <div>
      <div>
      <h1>Login</h1>
      <form onSubmit={handleLogin}>
        <input type="email" placeholder="E-mail" className='email_textbox'/>
        <input type="password" placeholder="Password"className='pass_textbox' />
        <div>
        <button type="submit" className='login'>Login</button>
        </div>
      </form>
      </div>
    </div>
  );
}

export default Login;
