// App.js
import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate, useLocation } from 'react-router-dom';
import axios from 'axios';

import Login from './components/Login';
import Registration from './components/Registration';
import ManufacturerDashboard from './components/ManufacturerDashboard';
import TransporterDashboard from './components/TransporterDashboard';
import MessagesList from './components/MessagesList';

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userRole, setUserRole] = useState('');

  useEffect(() => {
    const checkAuthStatus = async () => {
      try {
        const token = localStorage.getItem('token');
        if (token) {
          // Verify the JWT token on the server
          const res = await axios.get('/api/auth/verify', { headers: { Authorization: `Bearer ${token}` } });
          if (res.data.isAuthenticated) {
            setIsAuthenticated(true);
            setUserRole(res.data.role);
          }
        }
      } catch (err) {
        console.error(err.response.data);
      }
    };
    checkAuthStatus();
  }, []);

  const PrivateRoute = ({ path, element }) => {
    if (isAuthenticated) {
      return element;
    } else {
      return <Navigate to="/login" replace />;
    }
  };

  const AuthRoute = ({ path, element }) => {
    if (isAuthenticated) {
      return <Navigate to="/" replace />;
    } else {
      return element;
    }
  };

  return (
    <Router>
      <Routes>
        <Route path="/login" element={<Login />} />
        <Route path="/registration" element={<Registration />} />
        <Route path="/">
          <PrivateRoute path="manufacturer-dashboard" element={<ManufacturerDashboard />} />
          <PrivateRoute path="transporter-dashboard" element={<TransporterDashboard />} />
          <PrivateRoute path="messages" element={<MessagesList />} />
          <AuthRoute path="/" element={<Navigate to="/login" replace />} />
        </Route>
      </Routes>
    </Router>
  );
};

export default App;
