import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ManufacturerDashboard() {
  const [messages, setMessages] = useState([]);
  const [orderID, setOrderID] = useState('');
  const [to, setTo] = useState('');
  const [from, setFrom] = useState('');
  const [quantity, setQuantity] = useState('');
  const [address, setAddress] = useState('');
  const [transporter, setTransporter] = useState('');

  const fetchMessages = async () => {
    try {
      const token = localStorage.getItem('token');
      const decodedToken = parseJwt(token);
      const response = await axios.get(`http://localhost:5000/manufacturer/${decodedToken.email}/messages`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setMessages(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  // Function to parse the JWT token and extract the role
  const parseJwt = (token) => {
    // implementation
  };

  useEffect(() => {
    fetchMessages();
  }, []);

  const handleSendMessage = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');

      // Generate the alphanumeric code for Order ID
      const generatedOrderID = generateOrderID();

      await axios.post(
        'http://localhost:5000/api/messages',
        { orderID: generatedOrderID, to, from, quantity, address, transporter },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      // Clear form fields after sending the message
      setOrderID('');
      setTo('');
      setFrom('');
      setQuantity('');
      setAddress('');
      setTransporter('');
      // Fetch messages again to display the updated list
      fetchMessages();
    } catch (error) {
      console.error(error);
    }
  };

  // Generate a random alphanumeric code for Order ID
  const generateOrderID = () => {
    const characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789';
    let result = '';
    for (let i = 0; i < 5; i++) {
      result += characters.charAt(Math.floor(Math.random() * characters.length));
    }
    return result;
  };

  return (
    <div>
      <h1>Manufacturer Dashboard</h1>
      <h2>Messages</h2>
      {/* Rest of the code */}
      <h2>Send Message</h2>
      <form onSubmit={handleSendMessage}>
        <input type="text" placeholder="Order ID" value={orderID} onChange={(e) => setOrderID(e.target.value)} required readOnly/>
        {/* Rest of the code */}
      </form>
    </div>
  );
}

export default ManufacturerDashboard;
