// App.js
import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Switch, Route, Redirect } from 'react-router-dom';
import axios from 'axios';

import Login from './components/Login';
import Registration from './components/Registration';
import ManufacturerDashboard from './components/ManufacturerDashboard';
import TransporterDashboard from './components/TransporterDashboard';
import MessagesList from './components/MessagesList';

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userRole, setUserRole] = useState('');

  useEffect(() => {
    const checkAuthStatus = async () => {
      try {
        const token = localStorage.getItem('token');
        if (token) {
          // Verify the JWT token on the server
          const res = await axios.get('/api/auth/verify', { headers: { Authorization: `Bearer ${token}` } });
          if (res.data.isAuthenticated) {
            setIsAuthenticated(true);
            setUserRole(res.data.role);
          }
        }
      } catch (err) {
        console.error(err.response.data);
      }
    };
    checkAuthStatus();
  }, []);

  const PrivateRoute = ({ component: Component, ...rest }) => (
    <Route
      {...rest}
      render={(props) =>
        isAuthenticated ? (
          <Component {...props} />
        ) : (
          <Redirect to="/login" />
        )
      }
    />
  );

  return (
    <Router>
      <Switch>
        <Route exact path="/login" component={Login} />
        <Route exact path="/registration" component={Registration} />
        <PrivateRoute exact path="/manufacturer-dashboard" component={ManufacturerDashboard} />
        <PrivateRoute exact path="/transporter-dashboard" component={TransporterDashboard} />
        <PrivateRoute exact path="/messages" component={MessagesList} />
      </Switch>
    </Router>
  );
};

export default App;
