// ManufacturerDashboard.js
import React, { useState } from 'react';
import axios from 'axios';

const ManufacturerDashboard = () => {
  const [orderID, setOrderID] = useState('');
  const [to, setTo] = useState('');
  const [from, setFrom] = useState('');
  const [quantity, setQuantity] = useState('');
  const [address, setAddress] = useState('');
  const [transporter, setTransporter] = useState('');

  const handleSendMessage = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('/api/messages', { orderID, to, from, quantity, address, transporter }, { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } });
      console.log(res.data);
      // Perform any additional actions after sending the message
    } catch (err) {
      console.error(err.response.data);
    }
  };

  return (
    <div>
      <h1>Manufacturer Dashboard</h1>
      <form onSubmit={handleSendMessage}>
        <input type="text" placeholder="Order ID" value={orderID} onChange={(e) => setOrderID(e.target.value)} required />
        <input type="text" placeholder="To" value={to} onChange={(e) => setTo(e.target.value)} required />
        <input type="text" placeholder="From" value={from} onChange={(e) => setFrom(e.target.value)} required />
        <select value={quantity} onChange={(e) => setQuantity(e.target.value)} required>
          <option value="">Select Quantity</option>
          <option value="1">1 ton</option>
          <option value="2">2 tons</option>
          <option value="3">3 tons</option>
        </select>
        <input type="text" placeholder="Address" value={address} onChange={(e) => setAddress(e.target.value)} required />
        <select value={transporter} onChange={(e) => setTransporter(e.target.value)} required>
          <option value="">Select Transporter</option>
          {/* Populate the transporter options dynamically */}
        </select>
        <button type="submit">Send</button>
      </form>
    </div>
  );
};

export default ManufacturerDashboard;
