// TransporterDashboard.js
import React, { useState } from 'react';
import axios from 'axios';

const TransporterDashboard = () => {
  const [orderID, setOrderID] = useState('');
  const [price, setPrice] = useState('');

  const handleReplyMessage = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post(`/api/messages/${orderID}/reply`, { price }, { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } });
      console.log(res.data);
      // Perform any additional actions after sending the reply
    } catch (err) {
      console.error(err.response.data);
    }
  };

  return (
    <div>
      <h1>Transporter Dashboard</h1>
      <form onSubmit={handleReplyMessage}>
        <select value={orderID} onChange={(e) => setOrderID(e.target.value)} required>
          <option value="">Select Order ID</option>
          {/* Populate the order ID options dynamically */}
        </select>
        <input type="number" placeholder="Price" value={price} onChange={(e) => setPrice(e.target.value)} required />
        <button type="submit">Reply</button>
      </form>
    </div>
  );
};

export default TransporterDashboard;
