// Registration.js
import React, { useState } from 'react';
import axios from 'axios';

const Registration = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('');

  const handleRegistration = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('/api/auth/register', { email, password, role });
      // Store the JWT token in local storage
      localStorage.setItem('token', res.data.token);
      // Redirect to the appropriate dashboard based on the user role
      // Example: if (res.data.role === 'Manufacturer') { redirect to Manufacturer Dashboard }
      // Example: if (res.data.role === 'Transporter') { redirect to Transporter Dashboard }
    } catch (err) {
      console.error(err.response.data);
    }
  };

  return (
    <div>
      <h1>Registration</h1>
      <form onSubmit={handleRegistration}>
        <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} required />
        <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} required />
        <select value={role} onChange={(e) => setRole(e.target.value)} required>
          <option value="">Select Role</option>
          <option value="Manufacturer">Manufacturer</option>
          <option value="Transporter">Transporter</option>
        </select>
        <button type="submit">Register</button>
      </form>
    </div>
  );
};

export default Registration;
