// TransporterDashboard.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function TransporterDashboard() {
  const [messages, setMessages] = useState([]);
  const [selectedOrderID, setSelectedOrderID] = useState('');
  const [price, setPrice] = useState('');

  const fetchMessages = async () => {
    try {
      const token = localStorage.getItem('token');
      const decodedToken = parseJwt(token);
      const response = await axios.get(`http://localhost:5000/transporter/${decodedToken.email}/messages`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setMessages(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  // Function to parse the JWT token and extract the role
  const parseJwt = (token) => {
    const base64Url = token.split('.')[1];
    const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
    const jsonPayload = decodeURIComponent(
      atob(base64)
        .split('')
        .map(function (c) {
          return '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2);
        })
        .join('')
    );

    return JSON.parse(jsonPayload);
  };

  useEffect(() => {
    fetchMessages();
  }, []);

  const handleReplyMessage = async (e, orderID, price) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      await axios.post(
        'http://localhost:5000/api/messages/reply',
        { orderID: orderID, price },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      // Clear form fields after replying to the message
      setSelectedOrderID('');
      setPrice('');
      // Fetch messages again to display the updated list
      fetchMessages();
    } catch (error) {
      console.error(error);
    }
  };

  const updatePriceInMessage = (messageId, price) => {
    setMessages((prevMessages) =>
      prevMessages.map((message) =>
        message._id === messageId ? { ...message, price } : message
      )
    );
  };

  return (
    <div>
      <h1>Transporter Dashboard</h1>
      <h2>Messages</h2>
      {messages.map((message) => (
        <div key={message._id}>
          <p>Order ID: {message.orderID}</p>
          <p>To: {message.to}</p>
          <p>From: {message.from}</p>
          <p>Quantity: {message.quantity}</p>
          <p>Address: {message.address}</p>
          <p>Transporter: {message.transporter}</p>
          {!message.price && (
            <form
              onSubmit={(e) => {
                handleReplyMessage(e, message.orderID, price);
              }}
            >
              <input
                type="text"
                placeholder="Price"
                value={price}
                onChange={(e) => setPrice(e.target.value)}
                required
              />
              <button type="submit">Reply</button>
            </form>
          )}
          {message.price && <p>Price: {message.price}</p>}
          <hr />
        </div>
      ))}
    </div>
  );
}

export default TransporterDashboard;
