// ManufacturerDashboard.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ManufacturerDashboard() {
  const [messages, setMessages] = useState([]);
  const [orderID, setOrderID] = useState('');
  const [to, setTo] = useState('');
  const [from, setFrom] = useState('');
  const [quantity, setQuantity] = useState('');
  const [address, setAddress] = useState('');
  const [transporter, setTransporter] = useState('');

  useEffect(() => {
    const fetchMessages = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:3000/api/messages', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setMessages(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchMessages();
  }, []);

  const handleSendMessage = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      await axios.post(
        'http://localhost:3000/api/messages',
        { orderID, to, from, quantity, address, transporter },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      // Clear form fields after sending the message
      setOrderID('');
      setTo('');
      setFrom('');
      setQuantity('');
      setAddress('');
      setTransporter('');
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h1>Manufacturer Dashboard</h1>
      <h2>Messages</h2>
      {messages.map((message) => (
        <div key={message._id}>
          <p>Order ID: {message.orderID}</p>
          <p>To: {message.to}</p>
          <p>From: {message.from}</p>
          <p>Quantity: {message.quantity}</p>
          <p>Address: {message.address}</p>
          <p>Transporter: {message.transporter}</p>
          {message.price && <p>Price: {message.price}</p>}
          <hr />
        </div>
      ))}
      <h2>Send Message</h2>
      <form onSubmit={handleSendMessage}>
        <input type="text" placeholder="Order ID" value={orderID} onChange={(e) => setOrderID(e.target.value)} required />
        <input type="text" placeholder="To" value={to} onChange={(e) => setTo(e.target.value)} required />
        <input type="text" placeholder="From" value={from} onChange={(e) => setFrom(e.target.value)} required />
        <input type="text" placeholder="Quantity" value={quantity} onChange={(e) => setQuantity(e.target.value)} required />
        <input type="text" placeholder="Address" value={address} onChange={(e) => setAddress(e.target.value)} required />
        <input type="text" placeholder="Transporter" value={transporter} onChange={(e) => setTransporter(e.target.value)} required />
        <button type="submit">Send Message</button>
      </form>
    </div>
  );
}

export default ManufacturerDashboard;
