import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ManufacturerDashboard = () => {
  const [messages, setMessages] = useState([]);
  const [orderID, setOrderID] = useState('');
  const [to, setTo] = useState('');
  const [from, setFrom] = useState('');
  const [quantity, setQuantity] = useState('');
  const [address, setAddress] = useState('');
  const [transporter, setTransporter] = useState('');

  useEffect(() => {
    const fetchMessages = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:5000/api/messages', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setMessages(response.data.filter(message => message.from.role === 'manufacturer'));
      } catch (error) {
        console.error(error);
      }
    };

    fetchMessages();
  }, []);

  const token = localStorage.getItem('token');

  const handleSendMessage = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      await axios.post(
        'http://localhost:5000/api/messages',
        { orderID, to, from, quantity, address, transporter },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      // Clear form fields after sending the message
      setOrderID('');
      setTo('');
      setFrom('');
      setQuantity('');
      setAddress('');
      setTransporter('');
    } catch (error) {
      console.error(error);
    }
  };

  // Generate an alphanumeric code for Order ID
  useEffect(() => {
    const generateOrderID = () => {
      const alphanumeric = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789';
      let code = '';
      for (let i = 0; i < 5; i++) {
        code += alphanumeric.charAt(Math.floor(Math.random() * alphanumeric.length));
      }
      setOrderID(code);
    };

    generateOrderID();
  }, []);

  // Auto-populate Address field from registration information
 /* useEffect(() => {
    const fetchManufacturerData = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:5000/api/manufacturer', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setAddress(response.data.address);
      } catch (error) {
        console.error(error);
      }
    };

    fetchManufacturerData();
  }, []);*/

  return (
    <div>
      <h1>Manufacturer Dashboard</h1>
      <h2>Messages</h2>
      {messages.map((message) => (
        <div key={message._id}>
          <p>Order ID: {message.orderID}</p>
          <p>To: {message.to}</p>
          <p>From: {message.from}</p>
          <p>Quantity: {message.quantity}</p>
          <p>Address: {message.address}</p>
          <p>Transporter: {message.transporter}</p>
          {message.price && <p>Price: {message.price}</p>}
          <hr />
        </div>
      ))}
      <h2>Send Message</h2>
      <form onSubmit={handleSendMessage}>
        <input type="text" placeholder="Order ID" value={orderID} onChange={(e) => setOrderID(e.target.value)} required readOnly />
        <input type="text" placeholder="To" value={to} onChange={(e) => setTo(e.target.value)} required />
        <input type="text" placeholder="From" value={from} onChange={(e) => setFrom(e.target.value)} required />
        <select value={quantity} onChange={(e) => setQuantity(e.target.value)} required>
          <option value="">Select Quantity</option>
          <option value="1 ton">1 ton</option>
          <option value="2 ton">2 ton</option>
          <option value="3 ton">3 ton</option>
        </select>
        <input type="text" placeholder="Address" value={address} onChange={(e) => setAddress(e.target.value)} required readOnly />
        <select value={transporter} onChange={(e) => setTransporter(e.target.value)} required>
          <option value="">Select Transporter</option>
          <option value="Transporter 1">Transporter 1</option>
          <option value="Transporter 2">Transporter 2</option>
          <option value="Transporter 3">Transporter 3</option>
        </select>
        <button type="submit">Send Message</button>
      </form>
    </div>
  );
};

export default ManufacturerDashboard;
