// App.js
import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Switch, Route, useHistory } from 'react-router-dom';
import axios from 'axios';

import Login from './components/Login';
import Registration from './components/Registration';
import ManufacturerDashboard from './components/ManufacturerDashboard';
import TransporterDashboard from './components/TransporterDashboard';
import MessagesList from './components/MessagesList';

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userRole, setUserRole] = useState('');
  const history = useHistory();

  useEffect(() => {
    const checkAuthStatus = async () => {
      try {
        const token = localStorage.getItem('token');
        if (token) {
          // Verify the JWT token on the server
          const res = await axios.get('/api/auth/verify', { headers: { Authorization: `Bearer ${token}` } });
          if (res.data.isAuthenticated) {
            setIsAuthenticated(true);
            setUserRole(res.data.role);
          } else {
            history.push('/login'); // Redirect to login if not authenticated
          }
        } else {
          history.push('/login'); // Redirect to login if no token
        }
      } catch (err) {
        console.error(err.response.data);
        history.push('/login'); // Redirect to login on error
      }
    };
    checkAuthStatus();
  }, [history]);

  return (
    <Router>
      <Switch>
        <Route exact path="/login" component={Login} />
        <Route exact path="/registration" component={Registration} />
        {isAuthenticated && userRole === 'manufacturer' && (
          <Route path="/manufacturer-dashboard" component={ManufacturerDashboard} />
        )}
        {isAuthenticated && userRole === 'transporter' && (
          <Route path="/transporter-dashboard" component={TransporterDashboard} />
        )}
        {isAuthenticated && (
          <Route path="/messages" component={MessagesList} />
        )}
        <Redirect to="/login" /> {/* Redirect to login for any other route */}
      </Switch>
    </Router>
  );
};

export default App;
