// Registration.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const  Registration = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [address, setAddress] = useState('');
  const [role, setRole] = useState('manufacturer');
  const history = useNavigate();

  const handleRegistration = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/register', { email, password, role, address });
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h1>Registration</h1>
      <form onSubmit={handleRegistration} action="POST">
        <input type="email" placeholder="Email"  onChange={(e) => setEmail(e.target.value)} required />
        <input type="password" placeholder="Password" onChange={(e) => setPassword(e.target.value)} required />
        <input type="address" placeholder="Address" onChange={(e) => setAddress(e.target.value)} required />
        <select onChange={(e) => setRole(e.target.value)}>
          <option value="manufacturer">Manufacturer</option>
          <option value="transporter">Transporter</option>
        </select>
        <button type="submit">Register</button>
      </form>
    </div>
  );
}

export default Registration;
