import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ManufacturerDashboard() {
  const [messages, setMessages] = useState([]);
  const [orderID, setOrderID] = useState('');
  const [to, setTo] = useState('');
  const [from, setFrom] = useState('');
  const [quantity, setQuantity] = useState('');
  const [address, setAddress] = useState('');
  const [transporter, setTransporter] = useState('');

  const generateOrderID = () => {
    // Generate a random alphanumeric code
    const alphanumeric = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789';
    let code = '';
    for (let i = 0; i < 5; i++) {
      code += alphanumeric.charAt(Math.floor(Math.random() * alphanumeric.length));
    }
    setOrderID(code);
  };

  const fetchMessages = async (user) => {
    try {
      const token = localStorage.getItem('token');
      const decodedToken = parseJwt(token);
      const response = await axios.get(`http://localhost:5000/manufacturer/${decodedToken.email}/messages`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setMessages(response.data);
    } catch (error) {
      console.error(error);
    }
  };

    const fetchAddress = () => {
    const token = localStorage.getItem('token');
    const decodedToken = parseJwt(token);
    setAddress(decodedToken.address);
  }
  

    // Function to parse the JWT token and extract the role
    const parseJwt = (token) => {
      const base64Url = token.split('.')[1];
      const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
      const jsonPayload = decodeURIComponent(
        atob(base64)
          .split('')
          .map(function (c) {
            return '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2);
          })
          .join('')
      );
  
      return JSON.parse(jsonPayload);
    };

  useEffect(() => {
    generateOrderID(); // Generate initial Order ID
    fetchMessages();
    fetchAddress();
  }, []);

  

  const handleSendMessage = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const decodedToken = parseJwt(token);
      await axios.post(
        'http://localhost:5000/api/messages',
        { orderID, to, from, quantity, address, transporter },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      // Clear form fields after sending the message
      setOrderID('');
      setTo('');
      setFrom('');
      setQuantity('');
      setAddress(decodedToken.address);
      setTransporter('');
      // Fetch messages again to display the updated list
      fetchMessages();
      // Generate a new Order ID
    generateOrderID();
    } catch (error) {
      console.error(error);
    }
  };

  return (
<div className="flex justify-center">
      <div className="w-full max-w-md">
        <h1 className="text-2xl font-bold mb-4">Manufacturer Dashboard</h1>
        <h2 className="text-xl font-bold mb-2">Messages</h2>
        {messages.map((message) => (
          <div className="bg-gray-100 p-4 mb-4 rounded" key={message._id}>
            <p className="text-gray-800">
              <span className="font-bold">Order ID:</span> {message.orderID}
            </p>
            <p className="text-gray-800">
              <span className="font-bold">To:</span> {message.to}
            </p>
            <p className="text-gray-800">
              <span className="font-bold">From:</span> {message.from}
            </p>
            <p className="text-gray-800">
              <span className="font-bold">Quantity:</span> {message.quantity}
            </p>
            <p className="text-gray-800">
              <span className="font-bold">Address:</span> {message.address}
            </p>
            <p className="text-gray-800">
              <span className="font-bold">Transporter:</span> {message.transporter}
            </p>
            {message.price && (
              <p className="text-gray-800">
                <span className="font-bold">Price:</span> {message.price}
              </p>
            )}
            <hr className="my-2" />
          </div>
        ))}
        <h2 className="text-xl font-bold mb-2">Send Message</h2>
        <form onSubmit={handleSendMessage} className="bg-gray-100 p-4 rounded">
          <input
            type="text"
            placeholder="Order ID"
            value={orderID}
            onChange={(e) => setOrderID(e.target.value)}
            required
            className="w-full mb-2 p-2 rounded"
          />
          <input
            type="text"
            placeholder="To"
            value={to}
            onChange={(e) => setTo(e.target.value)}
            required
            className="w-full mb-2 p-2 rounded"
          />
          <input
            type="text"
            placeholder="From"
            value={from}
            onChange={(e) => setFrom(e.target.value)}
            required
            className="w-full mb-2 p-2 rounded"
          />
          <select
            onChange={(e) => setQuantity(e.target.value)}
            required
            className="w-full mb-2 p-2 rounded"
          >
            <option value="">select Quantity</option>
            <option value="1 ton">1 ton</option>
            <option value="2 ton">2 ton</option>
            <option value="3 ton">3 ton</option>
          </select>
          <input
            type="text"
            placeholder="Address"
            value={address}
            onChange={(e) => setAddress(e.target.value)}
            required
            readOnly
            className="w-full mb-2 p-2 rounded"
          />
          <select
            onChange={(e) => setTransporter(e.target.value)}
            required
            className="w-full mb-2 p-2 rounded"
          >
            <option value="">Transporter</option>
            <option value="transporter 1">Transporter 1</option>
            <option value="transporter 2">Transporter 2</option>
            <option value="transporter 3">Transporter 3</option>
          </select>
          <button type="submit" className="bg-blue-500 text-white py-2 px-4 rounded">
            Send Message
          </button>
        </form>
      </div>
    </div>
  );
}
export default ManufacturerDashboard;
