// App.js
import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import axios from 'axios';

import Login from './components/Login';
import Registration from './components/Registration';
import ManufacturerDashboard from './components/ManufacturerDashboard';
import TransporterDashboard from './components/TransporterDashboard';
import MessagesList from './components/MessagesList';

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userRole, setUserRole] = useState('');

  useEffect(() => {
    const checkAuthStatus = async () => {
      try {
        const token = localStorage.getItem('token');
        if (token) {
          // Verify the JWT token on the server
          const res = await axios.get('/api/auth/verify', { headers: { Authorization: `Bearer ${token}` } });
          if (res.data.isAuthenticated) {
            setIsAuthenticated(true);
            setUserRole(res.data.role);
          }
        }
      } catch (err) {
        console.error(err.response.data);
      }
    };
    checkAuthStatus();
  }, []);

  return (
    <Router>
      <Routes>
        <Route exact path="/login" component={Login} />
        <Route exact path="/registration" component={Registration} />
        {isAuthenticated && (
          <>
            <Route exact path="/manufacturer-dashboard" component={ManufacturerDashboard} />
            <Route exact path="/transporter-dashboard" component={TransporterDashboard} />
            <Route exact path="/messages" component={MessagesList} />
          </>
        )}
        <Navigate to="/login" />
      </Routes>
    </Router>
  );
};

export default App;
